import React, { useState } from 'react';
import './.css'; // Подключаем стили модуля 
import img1 from "../img/banner228.png"

const Module = () => {
  // Создаем состояние для хранения текущего индекса изображения
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Массив с путями к изображениям
  const images = [{img1}, {img1}, {img1}];

  // Функция для изменения индекса изображения
  const changeImage = (direction) => {
    if (direction === 'next') {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    } else if (direction === 'prev') {
      setCurrentImageIndex((prevIndex) =>
        prevIndex === 0 ? images.length - 1 : prevIndex - 1
      );
    }
  };

  return (
    <div className="module-container">
      {/* Прямоугольник с фоновой фотографией */}
      <div className="background-image" style={{ backgroundImage: `url(${images[currentImageIndex]})` }}>
        {/* Кнопки для смены изображений */}
        <button className="prev-button" onClick={() => changeImage('prev')}>
          <img src="left-arrow-icon.png" alt="Previous" />
        </button>
        <button className="next-button" onClick={() => changeImage('next')}>
          <img src="right-arrow-icon.png" alt="Next" />
        </button>
      </div>
    </div>
  );
};

export default Module;
