import React, { useState } from 'react';
import './Content3.css'; // Подключаем стили модуля

// Пример изображений, измените пути на актуальные
import productImage1 from '../img/2123.png';
import icon from '../img/Shopping Cartw.png';

const ProductCarousel = () => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const products = [
    { id: 1, image: {}, article: '', name: '', price: '', className: '' ,cla},
    { id: 2, image: productImage1, article: 'Артикул 2', name: 'Название 2', price: '2000₽', className: 'background-item' },
    { id: 3, image: productImage1, article: 'Артикул 3', name: 'Название 3', price: '3000₽', className: 'background-item' },
    { id: 4, image: productImage1, article: 'Артикул 4', name: 'Название 4', price: '4000₽', className: 'background-item' },
    { id: 5, image: productImage1, article: 'Артикул 5', name: 'Название 5', price: '5000₽', className: 'background-item' }
  ];

  const handleScroll = (direction) => {
    if (direction === 'next') {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % products.length);
    } else if (direction === 'prev') {
      setCurrentIndex((prevIndex) =>
        prevIndex === 0 ? products.length - 1 : prevIndex - 1
      );
    }
  };

  return (
    <div className="carousel-container">
      <h2 className="carousel-title">Название</h2>
      <div className="carousel-wrapper">
        <button className="prev-button" onClick={() => handleScroll('prev')}>←</button>
        <div className="carousel-content" style={{ transform: `translateX(-${currentIndex * (100 / products.length)}%)` }}>
          {products.map(product => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt="Product" className="product-image" />
              <div className="product-article">{product.article}</div>
              <div className="product-name">{product.name}</div>
              <div className="product-price-container">
                <div className="product-price">{product.price}</div>
                <div className="product-icon-container">
                  <img src={icon} alt="Icon" className="product-icon" />
                </div>
              </div>
            </div>
          ))}
        </div>
        <button className="next-button" onClick={() => handleScroll('next')}>→</button>
      </div>
    </div>
  );
};

export default ProductCarousel;
