import React, { useState } from 'react';
import './Content1.css'; // Подключаем стили модуля 
import img1 from "../img/banner228.png" 
import img2 from "../img/Group 73727.png"

const Module = () => {
  // Создаем состояние для хранения текущего индекса изображения
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  // Массив с путями к изображениям
  const images = [{img1}, {img2}, img1];

  // Функция для перемещения изображений влево или вправо
  const moveImages = (direction) => {
    const container = document.getElementById('image-container');
    const containerWidth = container.clientWidth;
    const step = containerWidth / images.length;
    const newPosition = direction === 'next' ? container.scrollLeft + step : container.scrollLeft - step;
    container.scrollTo({
      left: newPosition,
      behavior: 'smooth' // Добавляем плавную анимацию
    });
  };

  return (
    <div className="module-container">
      <div className="image-container" id="image-container">
        {images.map((image, index) => (
          <img key={index} src={image} alt={`Image ${index + 1}`} />
        ))}
      </div>
      {/* Кнопки для смены изображений */}
      <button className="prev-button" onClick={() => moveImages('prev')}>
        &lt;
      </button>
      <button className="next-button" onClick={() => moveImages('next')}>
        &gt;
      </button>
    </div>
  );
};

export default Module;
