import React, { useState } from 'react';
import './Content3.css'; 
import cart1 from "../img/"

const ProductModule = () => {
  const [activeTab, setActiveTab] = useState('all');
  const [visibleProducts, setVisibleProducts] = useState(8); // –ù–∞—á–∞–ª—å–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –≤–∏–¥–∏–º—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤

  const products = {
    all: [
      { id: 1, image: 'path_to_image1', reviews: 10, name: 'Intermediate accumulator sleeve', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
    ],
    sale: [
      { id: 1, image: 'path_to_image2', reviews: 5, name: 'Sale product 1', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 2, image: 'path_to_image4', reviews: 3, name: 'Sale product 2', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 3, image: 'path_to_image6', reviews: 8, name: 'Sale product 3', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 4, image: 'path_to_image8', reviews: 2, name: 'Sale product 4', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
    ],
    trending: [
      { id: 1, image: 'path_to_image3', reviews: 20, name: 'Trending product 1', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 2, image: 'path_to_image5', reviews: 18, name: 'Trending product 2', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 3, image: 'path_to_image7', reviews: 16, name: 'Trending product 3', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 4, image: 'path_to_image9', reviews: 10, name: 'Trending product 4', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 5, image: 'path_to_image11', reviews: 12, name: 'Trending product 5', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 6, image: 'path_to_image1', reviews: 22, name: 'Trending product 6', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 7, image: 'path_to_image8', reviews: 25, name: 'Trending product 7', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
      { id: 8, image: 'path_to_image10', reviews: 19, name: 'Trending product 8', price: '–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É' },
    ],
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    setVisibleProducts(tab === 'all' ? 8 : products[tab].length); // –°–±—Ä–æ—Å –≤–∏–¥–∏–º—ã—Ö –ø—Ä–æ–¥—É–∫—Ç–æ–≤ –ø—Ä–∏ —Å–º–µ–Ω–µ –≤–∫–ª–∞–¥–∫–∏
  };

  const loadMoreProducts = () => {
    setVisibleProducts((prevVisibleProducts) => prevVisibleProducts + 4);
  };

  return (
    <div className="product-module-container">
      <div className="tabs-container">
        <button className="tab-button" onClick={() => handleTabClick('all')}>–ü–æ–¥–±–æ—Ä–∫–∞ —Ç–æ–≤–∞—Ä–æ–≤</button>
        <button className="tab-button" onClick={() => handleTabClick('sale')}>–ê–∫—Ü–∏–∏</button>
        <button className="tab-button" onClick={() => handleTabClick('trending')}>–°–µ–π—á–∞—Å –ø–æ–∫—É–ø–∞—é—Ç</button>
      </div>
      <div className="products-container">
        {products[activeTab].slice(0, visibleProducts).map((product) => (
          <div className="product-card" key={product.id}>
            <img src={product.image} alt={product.name} className="product-image" />
            <p className="product-reviews"><span className="star-icon">‚≠ê</span> {product.reviews} –æ—Ç–∑—ã–≤–æ–≤</p>
            <p className="product-name">{product.name}</p>
            <p className="product-price">–¶–µ–Ω–∞ –ø–æ –∑–∞–ø—Ä–æ—Å—É <span className="price-request">–ø–æ –∑–∞–ø—Ä–æ—Å—É</span></p>
            <div className="product-actions">
              <button className="add-to-cart-button"><span className="cart-icon">üõí</span> –í –∫–æ—Ä–∑–∏–Ω—É</button>
              <div className="product-icons">
                <span className="product-icon">‚ù§Ô∏è</span>
                <span className="product-icon">üîç</span>
                <span className="product-icon">üìã</span>
              </div>
            </div>
          </div>
        ))}
      </div>
      {visibleProducts < products[activeTab].length && (
        <button className="load-more-button" onClick={loadMoreProducts}>–ü–æ–∫–∞–∑–∞—Ç—å –µ—â–µ</button>
      )}
    </div>
  );
};

export default ProductModule;
