import React, { useState } from 'react';
import './Content3.css';

const ProductModule = () => {
  const [activeTab, setActiveTab] = useState('all');

  const products = {
    all: [
      { id: 1, image: 'path_to_image1', reviews: 10, name: 'Intermediate accumulator sleeve', price: 'Цена по запросу' }, 
      { id: 1, image: 'path_to_image1', reviews: 10, name: 'Intermediate accumulator sleeve', price: 'Цена по запросу' }, 
      { id: 1, image: 'path_to_image1', reviews: 10, name: 'Intermediate accumulator sleeve', price: 'Цена по запросу' },
      // Добавьте остальные продукты
    ],
    sale: [
      { id: 1, image: 'path_to_image2', reviews: 5, name: 'Sale product 1', price: 'Цена по запросу' },
      // Добавьте остальные продукты
    ],
    trending: [
      { id: 1, image: 'path_to_image3', reviews: 20, name: 'Trending product 1', price: 'Цена по запросу' },
      // Добавьте остальные продукты
    ],
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="product-module-container">
      <div className="tabs-container">
        <button className="tab-button" onClick={() => handleTabClick('all')}>Подборка товаров</button>
        <button className="tab-button" onClick={() => handleTabClick('sale')}>Акции</button>
        <button className="tab-button" onClick={() => handleTabClick('trending')}>Сейчас покупают</button>
      </div>
      <div className="products-container">
        {products[activeTab].map((product) => (
          <div className="product-card" key={product.id}>
            <img src={product.image} alt={product.name} className="product-image" />
            <p className="product-reviews"><span className="star-icon">⭐</span> {product.reviews} отзывов</p>
            <p className="product-name">{product.name}</p>
            <p className="product-price">Цена по запросу <span className="price-request">по запросу</span></p>
            <div className="product-actions">
              <button className="add-to-cart-button"><span className="cart-icon">🛒</span> В корзину</button>
              <div className="product-icons">
                <span className="product-icon">❤️</span>
                <span className="product-icon">🔍</span>
                <span className="product-icon">📋</span>
              </div>
            </div>
          </div>
        ))}
      </div>
      {activeTab === 'all' && <button className="load-more-button">Показать еще</button>}
    </div>
  );
};

export default ProductModule;
